INSERT INTO `zf_post` VALUES ('226', '434', '2019英国喜剧片《异地母子情》', '', '', '1', '1564801192', '0', '0', '<p><img src=\"/upload/caiji/20190803/1564801192-6721.jpg\" alt=\"异地母子情\"/><br/>导演：<a target=\"_blank\" href=\"/index.php?m=vod-search-directed-%E8%BE%9B%E8%BF%AA%C2%B7%E7%BF%A0%E6%B4%BE%E5%85%8B\">辛迪·翠派克</a>&nbsp;<br/>主演：<a target=\"_blank\" href=\"/index.php?m=vod-search-starring-%E5%AE%89%E5%90%89%E6%8B%89%C2%B7%E8%B4%9D%E5%A1%9E%E7%89%B9\">安吉拉·贝塞特</a>&nbsp;<a target=\"_blank\" href=\"/index.php?m=vod-search-starring-%E5%B8%95%E7%89%B9%E4%B8%BD%E5%A4%8F%C2%B7%E9%98%BF%E5%A5%8E%E7%89%B9\">帕特丽夏·阿奎特</a>&nbsp;<a target=\"_blank\" href=\"/index.php?m=vod-search-starring-%E6%9D%B0%E5%85%8B%C2%B7%E8%8E%B1%E8%A5%BF\">杰克·莱西</a>&nbsp;<a target=\"_blank\" href=\"/index.php?m=vod-search-starring-%E8%8F%B2%E4%B8%BD%E8%A5%BF%E6%8F%90%C2%B7%E9%9C%8D%E5%A4%AB%E6%9B%BC\">菲丽西提·霍夫曼</a>&nbsp;<a target=\"_blank\" href=\"/index.php?m=vod-search-starring-%E8%89%BE%E7%B1%B3%E8%8E%89%C2%B7%E7%89%B9%E9%87%8C%E6%A2%85%E5%9B%A0\">艾米莉·特里梅因</a>&nbsp;<a target=\"_blank\" href=\"/index.php?m=vod-search-starring-%E9%A9%AC%E9%87%8C%E5%A5%A5%C2%B7%E5%9D%8E%E9%80%9A\">马里奥·坎通</a>&nbsp;<a target=\"_blank\" href=\"/index.php?m=vod-search-starring-%E6%B5%B7%E8%92%82%C2%B7%E5%8A%A0%E5%BE%B7%E7%BA%B3\">海蒂·加德纳</a>&nbsp;<a target=\"_blank\" href=\"/index.php?m=vod-search-starring-%E8%8E%AB%E8%8E%89%C2%B7%E4%BC%AF%E7%BA%B3%E5%BE%B7\">莫莉·伯纳德</a>&nbsp;<a target=\"_blank\" href=\"/index.php?m=vod-search-starring-%E6%9D%B0%E5%85%8B%C2%B7%E9%9C%8D%E5%A4%AB%E6%9B%BC\">杰克·霍夫曼</a>&nbsp;<a target=\"_blank\" href=\"/index.php?m=vod-search-starring-%E5%BC%97%E5%85%B0%E5%85%8B%C2%B7%E5%BE%B7%C2%B7%E8%83%A1%E9%87%8C%E5%A5%A5\">弗兰克·德·胡里奥</a>&nbsp;<a target=\"_blank\" href=\"/index.php?m=vod-search-starring-%E5%87%AF%E7%89%B9%C2%B7%E4%BC%8A%E6%96%AF%E9%A1%BF\">凯特·伊斯顿</a>&nbsp;<a target=\"_blank\" href=\"/index.php?m=vod-search-starring-%E8%BE%9B%E5%A5%8E%C2%B7%E6%B2%83%E5%B0%94%E6%96%AF\">辛奎·沃尔斯</a>&nbsp;<a target=\"_blank\" href=\"/index.php?m=vod-search-starring-Dan\">Dan</a>&nbsp;<a target=\"_blank\" href=\"/index.php?m=vod-search-starring-Puck\">Puck</a>&nbsp;<a target=\"_blank\" href=\"/index.php?m=vod-search-starring-Ron\">Ron</a>&nbsp;<a target=\"_blank\" href=\"/index.php?m=vod-search-starring-Maestri\">Maestri</a>&nbsp;<br/>类型：<a target=\"_blank\" href=\"/vod-type-6-1.html\">喜剧片</a>&nbsp;<br/>制片国家/地区：<br/>评分：7.0分<br/>剧情简介：<br/>对于空巢老人来说，让孩子长大和与他们疏远又有什么区别呢？在今年的母亲节，感到被孩子们冷落、遗忘的几个老友卡罗尔（安吉拉·贝塞特饰演）、吉莉安（帕特丽夏·阿奎特饰演）和海伦（菲丽西提·霍夫曼饰演）决定前往纽约，与成年的儿子们重塑关系，在这过程中，她们意识到需要改变生活的人不仅仅是自己的儿子。这段旅程变成了一段重新发现之旅，让这些女性重新定义了与孩子、朋友、配偶的关系，最重要的是，重新定义与自己的关系。《异地母子情》探索了已为人母女性深沉的爱、欢乐以及真实生活，由辛迪·翠派克（《欲望都市》）担任编剧和导演。</p>', '', '', '', '0.00', '0.00', '0', '23', '', '匿名', '', '133', '0', '0', '', '0', '0');
